// 自動前進禁止
<D-Setting:NoStep>

// 行動主体と対象以外を透明化
<D-Motion:invisible>
condition = a != subject && a != b
performer = $gameParty.battleMembers().concat($gameTroop.aliveMembers())
</D-Motion>

// 接近
<D-Motion:near/>

// 武器振り
<D-Motion:attack/>

// アニメーション＆ウェイト
<D-Animation:wait/>

// 戻る
<D-Motion:return/>

// 透明化解除
<D-Motion:visible>
condition = a != subject && a != b
performer = $gameParty.battleMembers().concat($gameTroop.aliveMembers())
</D-Motion>

